echo "$(for i in $(seq 1 $(cat file.txt | awk '{print length($0)}' | tail -n 1)); do cat file.txt | cut -c $i-$i | sort | uniq -c | sort | tail -n 1 | awk '{print $2}'; done | tr -d '\n' | xargs echo "ibase=2;obase=A;" | bc) * $(for i in $(seq 1 $(cat file.txt | awk '{print length($0)}' | tail -n 1)); do cat file.txt | cut -c $i-$i | sort | uniq -c | sort | head -n 1 | awk '{print $2}'; done | tr -d '\n' | xargs echo "ibase=2;obase=A;" | bc)" | bc